mapper.update:
update help_doc_topic  


		is_publish =  #{isPublish,jdbcType=TINYINT}  


		,modify_time =  #{modifyTime,jdbcType=TIMESTAMP}  




		id = #{id,jdbcType=BIGINT} and 


		topict_code = #{topictCode,jdbcType=VARCHAR}

mapper.select:
select count(1) from help_doc_topic  


	id = #{id,jdbcType=BIGINT}


	topict_code = #{topictCode,jdbcType=VARCHAR}

select id,topict_code,topict_name,category_code,category_name,second_category_code,second_category_name, sort_num,content,is_publish,visit_url,create_id,create_name,create_time,modify_id,modify_name,modify_time from help_doc_topic
id = #{id,jdbcType=BIGINT} and
topict_code = #{topictCode,jdbcType=VARCHAR}

select count(1) from help_doc_topic  


	sort_num = #{sortNum,jdbcType=INTEGER}


	and second_category_code = #{secondCategoryCode,jdbcType=VARCHAR}

	select id,topict_code,topict_name,category_code,category_name,second_category_code,second_category_name,
    sort_num,content,is_publish,visit_url,create_id,create_name,create_time,modify_id,modify_name,modify_time  
	from help_doc_topic  
	where 1=1

		and topict_name = #{dto.topictName,jdbcType=VARCHAR}


		and category_code = #{dto.categoryCode,jdbcType=VARCHAR}


		and second_category_code = #{dto.secondCategoryCode,jdbcType=VARCHAR}




		and date_format(create_time,'%Y-%m-%d %H:%m:%s') >= #{dto.createStartTime,jdbcType=VARCHAR} 




		and date_format(create_time,'%Y-%m-%d %H:%m:%s') <= #{dto.createEndTime,jdbcType=VARCHAR} 




		and date_format(modify_time,'%Y-%m-%d %H:%m:%s') >= #{dto.modifyStartTime,jdbcType=VARCHAR} 




		and date_format(modify_time,'%Y-%m-%d %H:%m:%s') <= #{dto.modifyEndTime,jdbcType=VARCHAR} 


	order by sort_num  
	limit #{page.pageOffset},#{page.rows}
