mapper.select:
select

from item_sku_publish_info
where
sku_code = #{skuCode,jdbcType=VARCHAR}
AND is_box_flag = #{isBoxFlag, jdbcType=TINYINT}

SELECT
FROM item_sku_publish_info info WHERE info.is_visable = 0 AND info.is_automatic_visable = 1 AND info.automatic_starttime IS NOT NULL AND info.automatic_starttime between DATE_FORMAT(NOW(),'%Y-%m-%d %H:00:00') and NOW() AND mod(info.id, #{taskQueueNum}) in
#{taskId}
LIMIT #{rows}

SELECT
FROM item_sku_publish_info info WHERE info.is_visable = 1 AND info.is_automatic_visable = 1 AND info.automatic_endtime IS NOT NULL AND info.automatic_endtime between DATE_FORMAT(NOW(),'%Y-%m-%d %H:00:00') and NOW() AND mod(info.id, #{taskQueueNum}) in
#{taskId}
LIMIT #{rows}

SELECT min(mim_quantity) as mim_quantity, max(max_purchase_quantity) as max_purchase_quantity, min(is_purchase_limit) as is_purchase_limit, max(display_quantity-reserve_quantity) display_quantity FROM item_sku_publish_info WHERE 1=1
AND sku_id in
#{skuId}
