WHERE
1 = 1

    AND item.product_channel_code = #{entity.productChannelCode}


    AND item.item_status = #{entity.itemStatus}


    AND item.item_name LIKE CONCAT('%', '${entity.itemName}','%' )


    AND item.item_code LIKE CONCAT('%', '${entity.itemCode}','%' )


    AND item.cid in
    (

        #{item}

    )


    AND brand.brand_name LIKE CONCAT('%', '${entity.brandName}','%' )


    AND item.has_vip_price = #{entity.hasVipPrice}

mapper.select:
SELECT count(1) FROM item item LEFT JOIN item_sku sku ON item.item_id = sku.item_id LEFT JOIN item_category category ON category.cid = item.cid LEFT JOIN item_brand brand ON brand.brand_id = item.brand LEFT JOIN product_channel channel ON channel.channel_code = item.product_channel_code WHERE item.outer_channel_brand_code IS NOT NULL AND item.outer_channel_category_code IS NOT NULL AND item.product_channel_code = '3010'
AND item.is_pre_sale = #{isPreSale}
AND item.modified >= #{startTime}
AND item.modified <= #{endTime}
AND sku.sku_code = #{skuCode}
ORDER BY item.created desc

SELECT sku.sku_code, sku.sku_id, item.item_code, item.item_name, item.item_id, sku.outer_sku_id, item.seller_id, item.is_pre_sale, item.pre_sale_starttime, item.pre_sale_endtime, CASE WHEN item.item_status = 5 THEN 1 ELSE 0 END as isUpShelf, item.created as itemCreateTime, item.modified as itemModifyTime, channel.channel_code, channel.channel_name, brand.brand_id, brand.brand_name, brand.brand_key, brand.create_time as brandCreateTime, brand.modify_time as brandModifyTime, category.cid, category.c_name, category.create_time as categoryCreateTime, category.modify_time as categoryModifyTime FROM item item LEFT JOIN item_sku sku ON item.item_id = sku.item_id LEFT JOIN item_category category ON category.cid = item.cid LEFT JOIN item_brand brand ON brand.brand_id = item.brand LEFT JOIN product_channel channel ON channel.channel_code = item.product_channel_code WHERE item.outer_channel_brand_code IS NOT NULL AND item.outer_channel_category_code IS NOT NULL AND item.product_channel_code = '3010'
AND item.is_pre_sale = #{isPreSale}
AND item.modified >= #{startTime}
AND item.modified <= #{endTime}
AND sku.sku_code = #{skuCode}
ORDER BY item.modified desc
limit #{page.pageOffset} , #{page.rows}

select sku.sku_code as skuCode, ifnull(GROUP_CONCAT(scb.seller_id),'') AS sellerId from item t join item_sku sku on t.item_id=sku.item_id join seller_category_brand_shield scb on scb.shield_flag = 1 AND scb.third_category_id = t.cid AND scb.brand_id = t.brand where t.product_channel_code = '3010' and t.item_id in
#{item}
group by sku.sku_code
