mapper.update:
UPDATE item_spu
SET item_spu.delete_flag = #{entity.deleteFlag}
WHERE spu_id = #{entity.spuId,jdbcType=BIGINT}

mapper.select:
select * from item_spu spu where 1=0 and delete_flag=0
or spu.spu_id in
#{item}

select count(1) from item_spu spu join item_category cat on spu.category_id=cat.cid join item_brand brand on spu.brand_id=brand.brand_id where 1=1 and spu.delete_flag=0 and spu.erp_first_category_code is not null and spu.erp_five_category_code is not null and spu.erp_first_category_code !='' and spu.erp_five_category_code !='' and spu.erp_first_category_code !='0' and spu.erp_five_category_code !='0'
and (spu.spu_name like concat('%',#{productName},'%') or spu.spu_code like concat('%',#{productName},'%') )
and brand.brand_name=#{brandName}
and cat.c_name =#{categoryName}
and t.model_type=#{modelType}

select spu.spu_id as spuId, spu.spu_code as spuCode, spu.spu_name as itemName, cat.c_name as categoryName, brand.brand_name as brandName, spu.model_type as serial, spu.unit as unit, spu.gross_weight as grossWeight, spu.net_weight as netWeight, spu.length, spu.wide as width, spu.high as height, spu.erp_code as erpCode, spu.tax_rate as taxRate, ifnull((select 1 from item t2 where t2.item_spu_id=spu.spu_id and t2.seller_id=#{sellerId} and t2.item_status !=6 limit 1),0) as applyStatus from item_spu spu join item_category cat on spu.category_id=cat.cid join item_brand brand on spu.brand_id=brand.brand_id where 1=1 and spu.delete_flag=0 and spu.erp_first_category_code is not null and spu.erp_five_category_code is not null and spu.erp_first_category_code !='' and spu.erp_five_category_code !='' and spu.erp_first_category_code !='0' and spu.erp_five_category_code !='0'
and (spu.spu_name like concat('%',#{productName},'%') or spu.spu_code like concat('%',#{productName},'%') )
and brand.brand_name=#{brandName}
and cat.c_name =#{categoryName}
and t.model_type=#{modelType}
limit #{start},#{pageSize};

SELECT spu.spu_name, spu.spu_code, spu.model_type, category.cid, category.c_name, brand.brand_id, brand.brand_name, pic.picture_url FROM item_spu spu JOIN item_brand brand ON spu.brand_id = brand.brand_id JOIN item_category category ON category.cid = spu.category_id JOIN item_spu_picture pic ON spu.spu_id = pic.spu_id AND pic.is_first = 1 AND pic.delete_flag = 0 where 1 = 1 AND spu.delete_flag=0 AND spu.spu_name is not NULL AND spu.spu_name != ''
and spu_name like concat('%',#{param},'%') or c_name like concat('%',#{param},'%') or brand_name like concat('%',#{param},'%') or model_type like concat('%',#{param},'%')
LIMIT #{page.pageOffset} , #{page.rows}
