mapper.select:
select

from timelimited_info
where promotion_id = #{promotionId,
jdbcType=VARCHAR}
and level_code = #{levelCode, jdbcType=VARCHAR}

select count(1) from ( select promotion_info.promotion_id from promotion_info inner join promotion_accumulaty on (promotion_info.promotion_id = promotion_accumulaty.promotion_id) inner join timelimited_info on (promotion_accumulaty.promotion_id = timelimited_info.promotion_id and promotion_accumulaty.level_code = timelimited_info.level_code) where 1 = 1
and promotion_info.show_status = #{entity.showStatus}
and timelimited_info.seller_code = #{entity.sellerCode}
and #{entity.startTime} BETWEEN date_format(promotion_info.effective_time , '%Y-%m-%d') and date_format(promotion_info.invalid_time , '%Y-%m-%d') and date_format(promotion_info.effective_time , '%Y-%m-%d') <= #{entity.startTime} and date_format(promotion_info.invalid_time , '%Y-%m-%d') >= #{entity.endTime}
and promotion_accumulaty.delete_flag = 0 GROUP BY promotion_info.id) as tmp

select promotion_info.promotion_id promotionId, promotion_info.promotion_name promotionName, promotion_info.promotion_type promotionType, promotion_info.effective_time effectiveTime, promotion_info.invalid_time invalidTime, promotion_info.is_vip isVip, promotion_info.status status, promotion_info.show_status showStatus from promotion_info inner join promotion_accumulaty on (promotion_info.promotion_id = promotion_accumulaty.promotion_id) inner join timelimited_info on (promotion_accumulaty.promotion_id = timelimited_info.promotion_id and promotion_accumulaty.level_code = timelimited_info.level_code) where 1 = 1
and promotion_info.show_status = #{entity.showStatus}
and timelimited_info.seller_code = #{entity.sellerCode}
and #{entity.startTime} BETWEEN date_format(promotion_info.effective_time , '%Y-%m-%d') and date_format(promotion_info.invalid_time , '%Y-%m-%d') and date_format(promotion_info.effective_time , '%Y-%m-%d') <= #{entity.startTime} and date_format(promotion_info.invalid_time , '%Y-%m-%d') >= #{entity.endTime}
and promotion_accumulaty.delete_flag = 0 group by promotion_info.id order by promotion_info.modify_time desc
