mapper.select:
select b.business_id businessId, b.seller_id sellerId, b.category_id categoryId, b.brand_id brandId, b.audit_status auditStatus, m.id memberId, c.company_name companyName, c.artificial_person_name artificialPersonName, c.artificial_person_mobile artificialPersonMobile, b.customer_manager_id customerManagerId, c.location_addr as locationAddr, b.create_time createTime from member_company_info c,member_base_info m,business_relationship b where m.id = c.member_id and c.member_id = b.buyer_id and b.audit_status='0' and m.status='1' and (b.category_id !=0 and b.brand_id !=0) and c.buyer_seller_type = '1' and b.seller_id=#{curBelongSellerId}
and c.company_name like CONCAT('%',#{companyName},'%')
and m.id = #{memberId}
order by b.create_time asc

select m.id memberId, b.audit_status auditStatus, c.company_name companyName, b.customer_manager_id customerManagerId, c.artificial_person_name artificialPersonName, c.artificial_person_mobile artificialPersonMobile, c.location_addr as locationAddr from member_company_info c,member_base_info m,business_relationship b where m.id = c.member_id and c.member_id = b.buyer_id and b.audit_status='0' and m.status='1' and (b.category_id !=0 and b.brand_id !=0) and c.buyer_seller_type = '1' and b.seller_id=#{curBelongSellerId}
and c.company_name like CONCAT('%',#{companyName},'%')
order by b.create_time

select m.id memberId, c.company_name companyName, c.artificial_person_name artificialPersonName, c.artificial_person_mobile artificialPersonMobile, c.artificial_person_idcard artificialPersonIdcard, c.artificial_person_pic_src artificialPersonPicSrc, c.artificial_person_pic_back_src artificialPersonPicBackSrc, m.industry_category industryCategory, case when l.buyer_business_license_pic_src !='' then l.buyer_business_license_pic_src else l.buyer_guarantee_license_pic_src end as buyerBusinessLicensePicSrc, l.buyer_business_license_id buyerBusinessLicenseId, s.verify_status verifyStatus, c.location_addr as locationAddr, s.info_type infoType, s.sync_error_msg registProcess from (member_base_info m,member_company_info c,member_status_info s) left join member_licence_info l on m.id = l.member_id where m.id = c.member_id and m.id = s.member_id and (s.info_type='11' or s.info_type='12' or s.info_type='13' or s.info_type='14') and c.buyer_seller_type='1'
and m.id = #{memberId}
and c.company_name = #{companyName}
and m.cur_belong_seller_id = #{curBelongSellerId}
