mapper.select:
SELECT b.box_id boxId, co.company_name boxSellerName, b.buyer_id memberId, b.seller_id sellerId, co.company_name boxSellerName, c.company_name companyName, c.artificial_person_mobile artificialPersonMobile, m.belong_seller_id belongSellerId FROM member_base_info m LEFT JOIN box_relationship b ON m.id = b.buyer_id LEFT JOIN member_company_info c ON m.id = c.member_id AND c.buyer_seller_type='1' LEFT JOIN member_company_info co ON b.seller_id =co.member_id AND co.buyer_seller_type='2' WHERE b.delete_flag=0 and not exists (select ms.status_id from member_status_info ms where ms.member_id=m.id and ms.info_type=13 and ms.verify_status='3')
and c.company_name like CONCAT('%',#{companyName},'%')
and c.artificial_person_mobile like CONCAT('%',#{artificialPersonMobile},'%')
and co.company_name like CONCAT('%',#{boxSellerName},'%')
and b.box_id = #{boxId}
limit #{page.pageOffset},#{page.rows}

select c.member_id memberId,seller_type sellerType from member_company_info c,member_base_info m where m.id=c.member_id and m.status='1'
and m.seller_type='1'
and c.company_name =#{companyName} and c.buyer_seller_type=#{buyerSellerType}
