mapper.select:
select 

FROM member_activity_picture
WHERE 1=1 


	and picture_name =#{entity.pictureName}


	and picture_type =#{entity.pictureType}


	and date_format(create_time,'%Y-%m-%d') BETWEEN #{entity.entity.createStartTime} and  #{entity.createEndTime}


	and member_code =#{entity.memberCode}

	 order by id desc

	 limit #{pager.pageOffset} , #{pager.rows}

select count(1)
FROM member_activity_picture
WHERE 1=1 


	and picture_name =#{entity.pictureName}


	and picture_type =#{entity.pictureType}


	and date_format(create_time,'%Y-%m-%d') BETWEEN #{entity.entity.createStartTime} and  #{entity.createEndTime}


	and member_code =#{entity.memberCode}
