mapper.select:
select

from promotion_info
where 1 = 1

  and status in

    #{statusCode}

  and show_status in

    #{verifyStatusCode}

  and mod(id, #{entity.taskQueueNum}) in

    #{taskId}

select
from promotion_info where 1=1 and promotion_id in
#{promotionId}

select

from promotion_info
where promotion_id = #{promotionId, jdbcType=VARCHAR}

mapper.update:
update promotion_info
show_status = #{showStatus}, modify_id = #{modifyId,jdbcType=BIGINT}, modify_name = #{modifyName,jdbcType=VARCHAR}, modify_time = now(),
where promotion_id = #{promotionId, jdbcType=VARCHAR}

update promotion_info
status = #{status,jdbcType=VARCHAR}, modify_id = #{modifyId,jdbcType=BIGINT}, modify_name = #{modifyName,jdbcType=VARCHAR}, modify_time = now()
where promotion_id = #{promotionId,jdbcType=VARCHAR}

update promotion_info
show_status = #{showStatus,jdbcType=VARCHAR}, verifier_id = #{verifierId,jdbcType=BIGINT}, verifier_name = #{verifierName,jdbcType=VARCHAR}, verify_time = now(), verify_remark = #{verifyRemark,jdbcType=VARCHAR}, modify_id = #{modifyId,jdbcType=BIGINT}, modify_name = #{modifyName,jdbcType=VARCHAR}, modify_time = now()
where promotion_id = #{promotionId,jdbcType=VARCHAR}

mapper.select:
select

from promotion_info
where promotion_type = #{promotionType, jdbcType=VARCHAR}
and  status <> #{deleteStatus, jdbcType=VARCHAR} 
and  now() <= invalid_time

select

from promotion_info
where promotion_type = #{entity.promotionType, jdbcType=VARCHAR}
and  status <> #{entity.deleteStatus, jdbcType=VARCHAR} 
and  now() >= invalid_time
and  show_status = #{entity.showStatus, jdbcType=VARCHAR}
and  promotion_provider_seller_code = #{sellerCode, jdbcType=VARCHAR}
and exists (select 1
from timelimited_info
where promotion_info.promotion_id = timelimited_info.promotion_id
and timelimited_info.sku_code = #{entity.skuCode, jdbcType=VARCHAR}
)

select
from promotion_info where has_redis_clean = 0
and promotion_type = #{entity.promotionType}
and invalid_time < now() and mod(id, #{entity.taskQueueNum}) in
#{taskId}
order by id desc

SELECT a.promotion_id, a.promotion_type, a.promotion_name, a.effective_time, a.invalid_time, a.`status`, (SELECT count(1) from buyer_winning_record bw where bw.promotion_id = a.promotion_id ) as winningNum, (SELECT count(1) from buyer_winning_record bw where bw.promotion_id = a.promotion_id and to_days(bw.winning_time) = to_days(now())) as winningTodayNum, a.modify_time FROM promotion_info a WHERE a.promotion_type = '22'
and a.promotion_name = #{entity.promotionName}
and a.`status` = #{entity.status}
and #{entity.effectiveTimestr} BETWEEN a.effective_time and a.invalid_time and a.effective_time <= #{entity.effectiveTimestr} and a.invalid_time >= #{entity.invalidTimestr}
and a.modify_time = #{entity.modifyTime}
limit #{pager.pageOffset},#{pager.rows}

  	SELECT 
  	count(1)
	FROM promotion_info a WHERE a.promotion_type = '22'

	and  a.promotion_name = #{entity.promotionName}


	and  a.`status` = #{entity.status}


		and #{entity.effectiveTimestr} BETWEEN a.effective_time and a.invalid_time
		and a.effective_time <= #{entity.effectiveTimestr} and a.invalid_time >= #{entity.invalidTimestr}


   	and a.modify_time = #{entity.modifyTime}

select * from promotion_info
where status != 9

	AND promotion_provider_seller_code = #{dto.promotionProviderSellerCode}


	AND promotion_name like concat('%', #{dto.promotionName} ,'%')


	AND status = #{dto.status}
	AND has_up_flag = 1


	AND has_up_flag = #{dto.hasUpFlag}


	AND invalid_time >= #{dto.effectiveTime}


 	AND effective_time <= #{dto.invalidTime}

order by create_time desc

SELECT COUNT(0)
FROM promotion_info
WHERE status != 9

	AND promotion_provider_seller_code = #{dto.promotionProviderSellerCode}


	AND promotion_name like concat('%', #{dto.promotionName} ,'%')


	AND status = #{dto.status}
	AND has_up_flag = 1 


	AND has_up_flag = #{dto.hasUpFlag}


	AND invalid_time >= #{dto.effectiveTime}


 	AND effective_time <= #{dto.invalidTime}

mapper.update:
UPDATE promotion_info SET show_status = #{dto.showStatus,jdbcType=VARCHAR},
status = #{dto.status,jdbcType=VARCHAR},
has_up_flag = 1, modify_id = #{dto.operatorId,jdbcType=BIGINT}, modify_name = #{dto.operatorName,jdbcType=VARCHAR}, modify_time = now() WHERE promotion_id = #{dto.promotionId,jdbcType=VARCHAR}

