mapper.select:

select * from promotion_bargain_info where
promotion_id = #{promotionId,jdbcType=VARCHAR} 
and level_code = #{levelCode,jdbcType=VARCHAR}

mapper.update:
update promotion_accumulaty
delete_flag = 1, modify_id = #{modifyId,jdbcType=BIGINT}, modify_name = #{modifyName,jdbcType=VARCHAR}, modify_time = now(),
where promotion_id = #{promotionId,jdbcType=VARCHAR} and level_code = #{levelCode,jdbcType=VARCHAR}
