mapper.select:
select e.user_id as userId, u.login_id as loginId, u.name as name, s.name as substationName, u.mobile as mobile from employee e left join user u on u.id =e.user_id left join substation s on s.substation_id = e.substation_id where e.deleted_flag = 0 and u.deleted_flag = 0 and s.deleted_flag = 0 and e.substation_id = #{substationId}
and u.name like concat('%', #{name}, '%')
limit #{pager.pageOffset},#{pager.rows}

select e.user_id as userId, u.login_id as loginId, e.substation_id as substationId, u.name as name, s.manager as manager, s.`name` as substationName, u.login_id as ioginId, u.mobile as mobile from employee e left join user u on u.id =e.user_id left join substation s on s.substation_id = e.substation_id where e.deleted_flag = 0 and u.deleted_flag = 0 and s.deleted_flag = 0
and e.substation_id = #{substationId}
and u.name like concat('%', #{name}, '%')
limit #{pager.pageOffset},#{pager.rows}

select count(0) from employee e left join user u on u.id =e.user_id left join substation s on s.substation_id = e.substation_id where e.deleted_flag = 0 and u.deleted_flag = 0 and s.deleted_flag = 0
and e.substation_id = #{substationId}
and u.name like concat('%', #{name}, '%')

select e.user_id as userId,e.company_id as companyId,u.`name` as name,h.parent_company_id as parentCompanyId , e.incharge_companies as inchargeCompanies,e.emp_no as empNo ,h.type as companyType,h.`name` as companyName, e.is_customer_manager as isCustomerManager,u.valid_flag as validFlag,e.gw from employee e left join user u on e.user_id = u.id left join htd_company h on e.company_id = h.company_id where e.deleted_flag = 0 and u.deleted_flag = 0 and (e.status='0' or e.status='1')
and (h.parent_company_id = #{htdDto.companyId} or h.company_id = #{htdDto.companyId})
and u.name like concat('%', #{dto.name}, '%')
and h.name like concat('%', #{dto.companyName}, '%')
and e.emp_no = #{dto.empNo}
and e.is_customer_manager = #{dto.isCustomerManager}
and e.gw = #{dto.gw}
limit #{pager.pageOffset},#{pager.rows}

select count(0) from employee e left join user u on e.user_id = u.id left join htd_company h on e.company_id = h.company_id where e.deleted_flag = 0 and u.deleted_flag = 0 and (e.status='0' or e.status='1')
and (h.parent_company_id = #{htdDto.companyId} or h.company_id = #{htdDto.companyId})
and u.name like concat('%', #{dto.name}, '%')
and h.name like concat('%', #{dto.companyName}, '%')
and e.emp_no = #{dto.empNo}
and e.is_customer_manager = #{dto.isCustomerManager}
and e.gw = #{dto.gw}
